var JaguarDb = require('./index.js').JaguarDb;
var db = new JaguarDb();

db.connect('./data', function(err) {

  if(err) {
    console.log('Could not connect: ' + err);
    return;
  }

  console.log('Connected!');

  var data = {title: 'hello', content: 'blah blah blah'};
  db.insert(data, function(err, insertedData) {
    if(err) {
      console.log('ERROR: ' + err);
      return;
    }
    console.log('Inserted');
    console.dir(insertedData);
  });

  var query = {};
  var fields = {};
  db.find(query, fields, function(err, documents) {
    if(err) {
      console.log('ERROR: ' + err);
      return;
    }

    console.log('Found %s documents', documents.length);
    console.dir(documents);
  });

});




